+ private DescriptiveStatistics performanceStats;
+
+ public ClientMain() {
+ INSTANCE = this;
+ performanceStats = new DescriptiveStatistics(100); // Keep track of last 100 measurements
+ }
+
+ public static ClientMain getInstance() {
+ return INSTANCE;
+ }
+
+ @Override
+ public void onInitializeClient() {
+ LOGGER.info("Initializing Er Sambucone Client...");
+ 
+ // Initialize directories
+ initDirectories();
+ 
+ // Initialize components
+ guiManager = new GuiManager();
+ inputHandler = new InputHandler();
+ skyblockLearner = new SkyblockLearner();
+ 
+ // Register keybindings
+ registerKeybindings();
+ 
+ // Register event handlers
+ registerEventHandlers();
+ 
+ LOGGER.info("Er Sambucone Client initialized successfully!");
+ }
+ 
+ private void initDirectories() {
+ File configDir = new File("config/ersambucone");
+ File aiDir = new File("config/ersambucone/ai");
+ 
+ if (!configDir.exists()) {
+ configDir.mkdirs();
+ LOGGER.info("Created config directory");
+ }
+ 
+ if (!aiDir.exists()) {
+ aiDir.mkdirs();
+ LOGGER.info("Created AI directory");
+ }
+ }
+ 
+ private void registerKeybindings() {
+ // Register keybindings through the InputHandler
+ inputHandler.registerKeybindings();
+ }
+ 
+ private void registerEventHandlers() {
+ // Register client tick event
+ ClientTickEvents.END_CLIENT_TICK.register(client -> {
+ // Update components on client tick
+ guiManager.update();
+ });
+ }
+ 
+ public GuiManager getGuiManager() {
+ return guiManager;
+ }
+ 
+ public SkyblockLearner getSkyblockLearner() {
+ return skyblockLearner;
+ }
+ 
+ public Gson getGson() {
+ return gson;
+ }
+ 
+ public void recordPerformanceMetric(double value) {
+ performanceStats.addValue(value);
+ }
+ 
+ public double getAveragePerformance() {
+ return performanceStats.getMean();
+ }
+ 
+ public void shutdown() {
+ if (skyblockLearner != null) {
+ skyblockLearner.shutdown();
+ }
+ LOGGER.info("Er Sambucone Client shutting down...");
+ }
