+package com.ersambucone.ui.clickgui;
+
+import com.ersambucone.Ersambucone;
+import com.ersambucone.module.Module;
+import com.ersambucone.module.ModuleCategory;
+import com.ersambucone.module.ModuleManager;
+import net.minecraft.client.gui.DrawContext;
+import net.minecraft.client.gui.screen.Screen;
+import net.minecraft.text.Text;
+
+import java.awt.*;
+import java.util.ArrayList;
+import java.util.List;
+
+public class ClickGUI extends Screen {
+ private final List<CategoryPanel> panels;
+ private CategoryPanel draggingPanel = null;
+ private int dragX, dragY;
+
+ public ClickGUI() {
+ super(Text.literal("ClickGUI"));
+ panels = new ArrayList<>();
+ 
+ // Initialize panels for each category
+ int startX = 10;
+ for (ModuleCategory category : ModuleCategory.values()) {
+ panels.add(new CategoryPanel(category, startX, 30));
+ startX += 120; // Space between panels
+ }
+ }
+
+ @Override
+ public void render(DrawContext context, int mouseX, int mouseY, float delta) {
+ this.renderBackground(context, mouseX, mouseY, delta);
+ 
+ // Draw watermark
+ String watermark = Ersambucone.NAME + " " + Ersambucone.VERSION;
+ context.drawText(this.textRenderer, watermark, 5, 5, 0xFFFFFFFF, true);
+ 
+ // Draw panels
+ for (CategoryPanel panel : panels) {
+ panel.render(context, mouseX, mouseY);
+ }
+ 
+ super.render(context, mouseX, mouseY, delta);
+ }
+
+ @Override
+ public boolean mouseClicked(double mouseX, double mouseY, int button) {
+ if (button == 0) { // Left click
+ for (CategoryPanel panel : panels) {
+ // Check if clicking on panel header for dragging
+ if (panel.isMouseOverHeader((int) mouseX, (int) mouseY)) {
+ draggingPanel = panel;
+ dragX = (int) mouseX - panel.getX();
+ dragY = (int) mouseY - panel.getY();
+ return true;
+ }
+ 
+ // Check if clicking on a module
+ if (panel.mouseClicked((int) mouseX, (int) mouseY)) {
+ return true;
+ }
+ }
+ }
+ return super.mouseClicked(mouseX, mouseY, button);
+ }
+
+ @Override
+ public boolean mouseReleased(double mouseX, double mouseY, int button) {
+ if (button == 0 && draggingPanel != null) {
+ draggingPanel = null;
+ return true;
+ }
+ return super.mouseReleased(mouseX, mouseY, button);
+ }
+
+ @Override
+ public boolean mouseDragged(double mouseX, double mouseY, int button, double deltaX, double deltaY) {
+ if (button == 0 && draggingPanel != null) {
+ draggingPanel.setPosition((int) mouseX - dragX, (int) mouseY - dragY);
+ return true;
+ }
+ return super.mouseDragged(mouseX, mouseY, button, deltaX, deltaY);
+ }
+
+ @Override
+ public boolean shouldPause() {
+ return false;
+ }
