+/**
+ * A simple HTML renderer that converts HTML to a texture
+ * This is a simplified version that doesn't actually parse HTML
+ * but renders a representation of the ClickGui
+ */
+public class SimpleHTMLRenderer {
+ private static final int TEXTURE_WIDTH = 1024;
+ private static final int TEXTURE_HEIGHT = 768;
+ 
+ private final MinecraftClient client;
+ private final Identifier textureId;
+ private NativeImageBackedTexture texture;
+ private NativeImage image;
+ 
+ // UI state
+ private final Map<String, Boolean> moduleStates = new HashMap<>();
+ private final Map<String, Map<String, Object>> moduleSettings = new HashMap<>();
+ 
+ // Panel positions
+ private final Map<String, Point> panelPositions = new HashMap<>();
+ 
+ // Colors
+ private static final int BG_COLOR = 0xCC202020;
+ private static final int HEADER_COLOR = 0xFF303030;
+ private static final int MODULE_BG_COLOR = 0xFF252525;
+ private static final int MODULE_ACTIVE_COLOR = 0xFF553333;
+ private static final int TEXT_COLOR = 0xFFFFFFFF;
+ private static final int ACCENT_COLOR = 0xFFFF5555;
+ 
+ public SimpleHTMLRenderer() {
+ this.client = MinecraftClient.getInstance();
+ this.textureId = new Identifier("ersambucone", "textures/gui/clickgui");
+ 
+ // Initialize default module states
+ initializeDefaultState();
+ 
+ // Create the texture
+ createTexture();
+ }
+ 
+ private void initializeDefaultState() {
+ // Initialize panel positions
+ panelPositions.put("Combat", new Point(50, 50));
+ panelPositions.put("Movement", new Point(270, 50));
+ panelPositions.put("Render", new Point(490, 50));
+ panelPositions.put("Player", new Point(50, 300));
+ panelPositions.put("World", new Point(270, 300));
+ panelPositions.put("Misc", new Point(490, 300));
+ 
+ // Initialize some example modules
+ String[] combatModules = {"KillAura", "Criticals", "AutoTotem"};
+ String[] movementModules = {"Speed", "Flight", "Sprint"};
+ String[] renderModules = {"ESP", "Fullbright", "Tracers"};
+ String[] playerModules = {"AutoEat", "FastPlace", "NoFall"};
+ String[] worldModules = {"XRay", "Nuker", "ChestESP"};
+ String[] miscModules = {"ClickGUI", "HUD", "Notifications"};
+ 
+ // Set default states
+ for (String module : combatModules) moduleStates.put(module, false);
+ for (String module : movementModules) moduleStates.put(module, false);
+ for (String module : renderModules) moduleStates.put(module, false);
+ for (String module : playerModules) moduleStates.put(module, false);
+ for (String module : worldModules) moduleStates.put(module, false);
+ for (String module : miscModules) moduleStates.put(module, false);
+ 
+ // Enable some modules by default
+ moduleStates.put("AutoTotem", true);
+ moduleStates.put("Sprint", true);
+ moduleStates.put("Fullbright", true);
+ moduleStates.put("NoFall", true);
+ moduleStates.put("ChestESP", true);
+ moduleStates.put("HUD", true);
+ 
+ // Add some example settings
+ Map<String, Object> killAuraSettings = new HashMap<>();
+ killAuraSettings.put("Range", 4.2f);
+ killAuraSettings.put("CPS", 12);
+ killAuraSettings.put("AutoBlock", true);
+ killAuraSettings.put("Priority", "Distance");
+ moduleSettings.put("KillAura", killAuraSettings);
+ 
+ Map<String, Object> speedSettings = new HashMap<>();
+ speedSettings.put("Mode", "Vanilla");
+ speedSettings.put("Speed", 1.0f);
+ moduleSettings.put("Speed", speedSettings);
+ 
+ Map<String, Object> espSettings = new HashMap<>();
+ espSettings.put("Mode", "2D");
+ espSettings.put("Players", true);
+ espSettings.put("Mobs", false);
+ espSettings.put("Items", true);
+ moduleSettings.put("ESP", espSettings);
+ }
+ 
+ private void createTexture() {
+ try {
+ // Create a new image
+ image = new NativeImage(TEXTURE_WIDTH, TEXTURE_HEIGHT, true);
+ 
+ // Clear the image
+ for (int x = 0; x < TEXTURE_WIDTH; x++) {
+ for (int y = 0; y < TEXTURE_HEIGHT; y++) {
+ image.setColor(x, y, 0x00000000);
+ }
+ }
+ 
+ // Create the texture
+ texture = new NativeImageBackedTexture(image);
+ 
+ // Register the texture
+ MinecraftClient.getInstance().getTextureManager().registerTexture(textureId, texture);
+ 
+ // Render the UI to the texture
+ renderUI();
+ } catch (Exception e) {
+ MainMod.LOGGER.error("Failed to create ClickGui texture", e);
+ }
+ }
+ 
+ public void renderUI() {
+ if (image == null) return;
+ 
+ // Clear the image
+ for (int x = 0; x < TEXTURE_WIDTH; x++) {
+ for (int y = 0; y < TEXTURE_HEIGHT; y++) {
+ image.setColor(x, y, 0x00000000);
+ }
+ }
+ 
+ // Draw watermark
+ drawText("Ersambucone Client " + MainMod.VERSION, 10, 10, TEXT_COLOR);
+ 
+ // Draw each panel
+ for (Map.Entry<String, Point> entry : panelPositions.entrySet()) {
+ String category = entry.getKey();
+ Point position = entry.getValue();
+ 
+ drawPanel(category, position.x, position.y);
+ }
+ 
+ // Update the texture
+ texture.upload();
+ }
+ 
+ private void drawPanel(String category, int x, int y) {
+ final int PANEL_WIDTH = 200;
+ final int HEADER_HEIGHT = 30;
+ 
+ // Get modules for this category
+ String[] modules;
+ switch (category) {
+ case "Combat":
+ modules = new String[]{"KillAura", "Criticals", "AutoTotem"};
+ break;
+ case "Movement":
+ modules = new String[]{"Speed", "Flight", "Sprint"};
+ break;
+ case "Render":
+ modules = new String[]{"ESP", "Fullbright", "Tracers"};
+ break;
+ case "Player":
+ modules = new String[]{"AutoEat", "FastPlace", "NoFall"};
+ break;
+ case "World":
+ modules = new String[]{"XRay", "Nuker", "ChestESP"};
+ break;
+ case "Misc":
+ modules = new String[]{"ClickGUI", "HUD", "Notifications"};
+ break;
+ default:
+ modules = new String[0];
+ }
+ 
+ // Calculate panel height based on number of modules
+ int moduleHeight = 30;
+ int panelHeight = HEADER_HEIGHT + (modules.length * moduleHeight);
+ 
+ // Draw panel background
+ drawRect(x, y, PANEL_WIDTH, panelHeight, BG_COLOR);
+ 
+ // Draw header
+ drawRect(x, y, PANEL_WIDTH, HEADER_HEIGHT, HEADER_COLOR);
+ drawText(category, x + PANEL_WIDTH / 2 - 20, y + 10, TEXT_COLOR);
+ 
+ // Draw modules
+ for (int i = 0; i < modules.length; i++) {
+ String module = modules[i];
+ boolean enabled = moduleStates.getOrDefault(module, false);
+ 
+ int moduleY = y + HEADER_HEIGHT + (i * moduleHeight);
+ 
+ // Draw module background
+ drawRect(x + 5, moduleY + 5, PANEL_WIDTH - 10, moduleHeight - 10, 
+ enabled ? MODULE_ACTIVE_COLOR : MODULE_BG_COLOR);
+ 
+ // Draw module name
+ drawText(module, x + 10, moduleY + 10, TEXT_COLOR);
+ 
+ // Draw toggle indicator
+ drawRect(x + PANEL_WIDTH - 25, moduleY + 10, 15, 15, 
+ enabled ? ACCENT_COLOR : 0xFF444444);
+ }
+ }
+ 
+ private void drawRect(int x, int y, int width, int height, int color) {
+ // Ensure we're within bounds
+ if (x < 0 || y < 0 || x + width > TEXTURE_WIDTH || y + height > TEXTURE_HEIGHT) {
+ return;
+ }
+ 
+ // Extract color components
+ int a = (color >> 24) & 0xFF;
+ int r = (color >> 16) & 0xFF;
+ int g = (color >> 8) & 0xFF;
+ int b = color & 0xFF;
+ 
+ // Draw the rectangle
+ for (int i = 0; i < width; i++) {
+ for (int j = 0; j < height; j++) {
+ int pixelX = x + i;
+ int pixelY = y + j;
+ 
+ // Set the pixel color
+ image.setColor(pixelX, pixelY, (a << 24) | (r << 16) | (g << 8) | b);
+ }
+ }
+ }
+ 
+ private void drawText(String text, int x, int y, int color) {
+ // This is a simplified text rendering method
+ // In a real implementation, you would use a font renderer
+ 
+ // For now, we'll just draw a colored rectangle to represent text
+ int textWidth = text.length() * 6;
+ int textHeight = 10;
+ 
+ drawRect(x, y, textWidth, textHeight, color);
+ }
+ 
+ public void render(DrawContext context, int mouseX, int mouseY) {
+ // Draw the texture
+ int screenWidth = client.getWindow().getScaledWidth();
+ int screenHeight = client.getWindow().getScaledHeight();
+ 
+ // Draw the texture centered on the screen
+ context.drawTexture(textureId, 0, 0, 0, 0, screenWidth, screenHeight, screenWidth, screenHeight);
+ }
+ 
+ public boolean mouseClicked(double mouseX, double mouseY, int button) {
+ // Check if clicking on a module
+ for (Map.Entry<String, Point> entry : panelPositions.entrySet()) {
+ String category = entry.getKey();
+ Point position = entry.getValue();
+ 
+ // Get modules for this category
+ String[] modules;
+ switch (category) {
+ case "Combat":
+ modules = new String[]{"KillAura", "Criticals", "AutoTotem"};
+ break;
+ case "Movement":
+ modules = new String[]{"Speed", "Flight", "Sprint"};
+ break;
+ case "Render":
+ modules = new String[]{"ESP", "Fullbright", "Tracers"};
+ break;
+ case "Player":
+ modules = new String[]{"AutoEat", "FastPlace", "NoFall"};
+ break;
+ case "World":
+ modules = new String[]{"XRay", "Nuker", "ChestESP"};
+ break;
+ case "Misc":
+ modules = new String[]{"ClickGUI", "HUD", "Notifications"};
+ break;
+ default:
+ modules = new String[0];
+ }
+ 
+ // Check if clicking on a module
+ for (int i = 0; i < modules.length; i++) {
+ String module = modules[i];
+ 
+ int x = position.x + 5;
+ int y = position.y + 30 + (i * 30) + 5;
+ int width = 200 - 10;
+ int height = 30 - 10;
+ 
+ if (mouseX >= x && mouseX <= x + width && mouseY >= y && mouseY <= y + height) {
+ // Toggle module
+ boolean currentState = moduleStates.getOrDefault(module, false);
+ moduleStates.put(module, !currentState);
+ 
+ // Update the UI
+ renderUI();
+ 
+ return true;
+ }
+ }
+ 
+ // Check if clicking on panel header (for dragging)
+ int headerX = position.x;
+ int headerY = position.y;
+ int headerWidth = 200;
+ int headerHeight = 30;
+ 
+ if (mouseX >= headerX && mouseX <= headerX + headerWidth && mouseY >= headerY && mouseY <= headerY + headerHeight) {
+ // Start dragging
+ return true;
+ }
+ }
+ 
+ return false;
+ }
+ 
+ public boolean mouseDragged(double mouseX, double mouseY, int button, double deltaX, double deltaY) {
+ // Implement panel dragging here
+ return false;
+ }
+ 
+ public void close() {
+ // Clean up resources
+ if (image != null) {
+ image.close();
+ image = null;
+ }
+ 
+ if (texture != null) {
+ texture.close();
+ texture = null;
+ }
+ }
