+ if (isDragging) {
+ panel.style.left = `${e.clientX - offsetX}px`;
+ panel.style.top = `${e.clientY - offsetY}px`;
+ }
+ });
+ 
+ document.addEventListener('mouseup', () => {
+ isDragging = false;
+ });
