+package com.ersambucone.ui;
+
+import com.ersambucone.utils.Logger;
+import net.minecraft.client.MinecraftClient;
+import net.minecraft.client.gui.DrawContext;
+import net.minecraft.client.gui.screen.Screen;
+import net.minecraft.client.render.*;
+import net.minecraft.client.texture.NativeImage;
+import net.minecraft.client.texture.NativeImageBackedTexture;
+import net.minecraft.client.util.math.MatrixStack;
+import net.minecraft.text.Text;
+import net.minecraft.util.Identifier;
+
+import java.awt.*;
+import java.io.BufferedReader;
+import java.io.IOException;
+import java.io.InputStream;
+import java.io.InputStreamReader;
+import java.nio.charset.StandardCharsets;
+import java.util.HashMap;
+import java.util.Map;
+import java.util.stream.Collectors;
+
+/**
+ * HTML-based ClickGui for Ersambucone client
+ */
+public class HTMLClickGui extends Screen {
+ private static final Identifier BACKGROUND_TEXTURE = new Identifier("ersambucone", "textures/gui/clickgui_bg.png");
+ private static final int TEXTURE_WIDTH = 1024;
+ private static final int TEXTURE_HEIGHT = 768;
+ 
+ // Panel positions
+ private final Map<String, Point> panelPositions = new HashMap<>();
+ private String draggingPanel = null;
+ private int dragOffsetX, dragOffsetY;
+ 
+ // Module states
+ private final Map<String, Boolean> moduleStates = new HashMap<>();
+ 
+ public HTMLClickGui() {
+ super(Text.literal("Ersambucone ClickGui"));
+ initializePanels();
+ initializeModules();
+ }
+ 
+ private void initializePanels() {
+ // Initialize panel positions
+ panelPositions.put("Rewards", new Point(50, 50));
+ panelPositions.put("Utility", new Point(270, 50));
+ panelPositions.put("Cosmetics", new Point(490, 50));
+ panelPositions.put("Automation", new Point(50, 300));
+ panelPositions.put("Monitoring", new Point(270, 300));
+ }
+ 
+ private void initializeModules() {
+ // Initialize module states
+ // Rewards modules
+ moduleStates.put("RewardTracker", false);
+ moduleStates.put("GiftAutomation", false);
+ 
+ // Utility modules
+ moduleStates.put("ChatLogger", true);
+ moduleStates.put("CommandSystem", true);
+ moduleStates.put("InventoryUtils", false);
+ 
+ // Cosmetics modules
+ moduleStates.put("CapeSystem", true);
+ moduleStates.put("ParticleFX", false);
+ 
+ // Automation modules
+ moduleStates.put("BuildHelper", false);
+ moduleStates.put("MacroEngine", true);
+ moduleStates.put("SmartClicker", false);
+ 
+ // Monitoring modules
+ moduleStates.put("StaffRadar", false);
+ moduleStates.put("FarmAnalytics", true);
+ }
+ 
+ @Override
+ public void render(DrawContext context, int mouseX, int mouseY, float delta) {
+ // Render a dark background
+ this.renderBackground(context, mouseX, mouseY, delta);
+ 
+ // Draw watermark
+ context.drawText(this.textRenderer, "Ersambucone Client", 10, 10, 0xFFFFFFFF, true);
+ 
+ // Draw each panel
+ for (Map.Entry<String, Point> entry : panelPositions.entrySet()) {
+ String category = entry.getKey();
+ Point position = entry.getValue();
+ 
+ drawPanel(context, category, position.x, position.y, mouseX, mouseY);
+ }
+ 
+ super.render(context, mouseX, mouseY, delta);
+ }
+ 
+ private void drawPanel(DrawContext context, String category, int x, int y, int mouseX, int mouseY) {
+ final int PANEL_WIDTH = 200;
+ final int HEADER_HEIGHT = 30;
+ 
+ // Get modules for this category
+ String[] modules;
+ switch (category) {
+ case "Rewards":
+ modules = new String[]{"RewardTracker", "GiftAutomation"};
+ break;
+ case "Utility":
+ modules = new String[]{"ChatLogger", "CommandSystem", "InventoryUtils"};
+ break;
+ case "Cosmetics":
+ modules = new String[]{"CapeSystem", "ParticleFX"};
+ break;
+ case "Automation":
+ modules = new String[]{"BuildHelper", "MacroEngine", "SmartClicker"};
+ break;
+ case "Monitoring":
+ modules = new String[]{"StaffRadar", "FarmAnalytics"};
+ break;
+ default:
+ modules = new String[0];
+ }
+ 
+ // Calculate panel height based on number of modules
+ int moduleHeight = 30;
+ int panelHeight = HEADER_HEIGHT + (modules.length * moduleHeight);
+ 
+ // Draw panel background
+ context.fill(x, y, x + PANEL_WIDTH, y + panelHeight, 0xCC202020);
+ 
+ // Draw header
+ context.fill(x, y, x + PANEL_WIDTH, y + HEADER_HEIGHT, 0xFF303030);
+ context.drawCenteredTextWithShadow(this.textRenderer, Text.literal(category), x + PANEL_WIDTH / 2, y + 10, 0xFFFFFFFF);
+ 
+ // Draw modules
+ for (int i = 0; i < modules.length; i++) {
+ String module = modules[i];
+ boolean enabled = moduleStates.getOrDefault(module, false);
+ 
+ int moduleY = y + HEADER_HEIGHT + (i * moduleHeight);
+ 
+ // Draw module background
+ int bgColor = enabled ? 0xFF553333 : 0xFF252525;
+ context.fill(x + 5, moduleY + 5, x + PANEL_WIDTH - 5, moduleY + moduleHeight - 5, bgColor);
+ 
+ // Draw module name
+ context.drawTextWithShadow(this.textRenderer, Text.literal(module), x + 10, moduleY + 10, 0xFFFFFFFF);
+ 
+ // Draw toggle indicator
+ int toggleColor = enabled ? 0xFFFF5555 : 0xFF444444;
+ context.fill(x + PANEL_WIDTH - 25, moduleY + 10, x + PANEL_WIDTH - 10, moduleY + 25, toggleColor);
+ }
+ }
+ 
+ @Override
+ public boolean mouseClicked(double mouseX, double mouseY, int button) {
+ if (button == 0) { // Left click
+ // Check if clicking on a panel header (for dragging)
+ for (Map.Entry<String, Point> entry : panelPositions.entrySet()) {
+ String category = entry.getKey();
+ Point position = entry.getValue();
+ 
+ int headerX = position.x;
+ int headerY = position.y;
+ int headerWidth = 200;
+ int headerHeight = 30;
+ 
+ if (mouseX >= headerX && mouseX <= headerX + headerWidth && 
+ mouseY >= headerY && mouseY <= headerY + headerHeight) {
+ draggingPanel = category;
+ dragOffsetX = (int) mouseX - position.x;
+ dragOffsetY = (int) mouseY - position.y;
+ return true;
+ }
+ 
+ // Check if clicking on a module
+ String[] modules;
+ switch (category) {
+ case "Rewards":
+ modules = new String[]{"RewardTracker", "GiftAutomation"};
+ break;
+ case "Utility":
+ modules = new String[]{"ChatLogger", "CommandSystem", "InventoryUtils"};
+ break;
+ case "Cosmetics":
+ modules = new String[]{"CapeSystem", "ParticleFX"};
+ break;
+ case "Automation":
+ modules = new String[]{"BuildHelper", "MacroEngine", "SmartClicker"};
+ break;
+ case "Monitoring":
+ modules = new String[]{"StaffRadar", "FarmAnalytics"};
+ break;
+ default:
+ modules = new String[0];
+ }
+ 
+ for (int i = 0; i < modules.length; i++) {
+ String module = modules[i];
+ 
+ int moduleX = position.x + 5;
+ int moduleY = position.y + 30 + (i * 30) + 5;
+ int moduleWidth = 200 - 10;
+ int moduleHeight = 30 - 10;
+ 
+ if (mouseX >= moduleX && mouseX <= moduleX + moduleWidth && 
+ mouseY >= moduleY && mouseY <= moduleY + moduleHeight) {
+ // Toggle module
+ boolean currentState = moduleStates.getOrDefault(module, false);
+ moduleStates.put(module, !currentState);
+ 
+ Logger.log("Toggled module: " + module + " to " + !currentState);
+ 
+ return true;
+ }
+ }
+ }
+ }
+ 
+ return super.mouseClicked(mouseX, mouseY, button);
+ }
+ 
+ @Override
+ public boolean mouseDragged(double mouseX, double mouseY, int button, double deltaX, double deltaY) {
+ if (button == 0 && draggingPanel != null) {
+ Point position = panelPositions.get(draggingPanel);
+ if (position != null) {
+ position.x = (int) mouseX - dragOffsetX;
+ position.y = (int) mouseY - dragOffsetY;
+ return true;
+ }
+ }
+ 
+ return super.mouseDragged(mouseX, mouseY, button, deltaX, deltaY);
+ }
+ 
+ @Override
+ public boolean mouseReleased(double mouseX, double mouseY, int button) {
+ if (button == 0 && draggingPanel != null) {
+ draggingPanel = null;
+ return true;
+ }
+ 
+ return super.mouseReleased(mouseX, mouseY, button);
+ }
+ 
+ @Override
+ public boolean shouldPause() {
+ return false;
+ }
