+package com.ersambucone;
+
+import com.ersambucone.ui.NexusVoidGUI;
+import com.ersambucone.utils.Logger;
+import net.fabricmc.api.ClientModInitializer;
+import net.fabricmc.fabric.api.client.event.lifecycle.v1.ClientTickEvents;
+import net.fabricmc.fabric.api.client.keybinding.v1.KeyBindingHelper;
+import net.minecraft.client.MinecraftClient;
+import net.minecraft.client.option.KeyBinding;
+import net.minecraft.client.util.InputUtil;
+import org.lwjgl.glfw.GLFW;
+
+/**
+ * Er Sambucone Client - A powerful Minecraft client with enhanced features
+ * 
+ * Features:
+ * - Advanced GUI with multiple themes
+ * - Module system for various functionalities
+ * - Customizable settings for each module
+ * - Keybindings for quick access
+ */
+public class ErSambuconeClient implements ClientModInitializer {
+ 
+ // Client instance
+ private static ErSambuconeClient INSTANCE;
+ 
+ // Keybindings
+ private KeyBinding guiKeyBinding;
+ 
+ // Client version
+ public static final String VERSION = "1.0.0";
+ 
+ @Override
+ public void onInitializeClient() {
+ INSTANCE = this;
+ 
+ Logger.log("Initializing Er Sambucone Client v" + VERSION);
+ 
+ // Register keybindings
+ registerKeybindings();
+ 
+ // Register event handlers
+ registerEventHandlers();
+ 
+ Logger.log("Er Sambucone Client initialized successfully!");
+ }
+ 
+ private void registerKeybindings() {
+ // GUI keybinding (Right Shift)
+ guiKeyBinding = KeyBindingHelper.registerKeyBinding(new KeyBinding(
+ "key.ersambucone.gui",
+ InputUtil.Type.KEYSYM,
+ GLFW.GLFW_KEY_RIGHT_SHIFT,
+ "key.categories.ersambucone"
+ ));
+ 
+ Logger.log("Registered Er Sambucone Client keybindings");
+ }
+ 
+ private void registerEventHandlers() {
+ // Tick event for handling keybindings
+ ClientTickEvents.END_CLIENT_TICK.register(client -> {
+ // Check GUI keybinding
+ if (guiKeyBinding.wasPressed()) {
+ openGUI();
+ }
+ });
+ 
+ Logger.log("Registered Er Sambucone Client event handlers");
+ }
+ 
+ /**
+ * Opens the Enhanced GUI
+ */
+ public void openGUI() {
+ MinecraftClient client = MinecraftClient.getInstance();
+ client.setScreen(new NexusVoidGUI());
+ Logger.log("Opened Er Sambucone Enhanced GUI");
+ }
+ 
+ /**
+ * Gets the client instance
+ * @return The Er Sambucone Client instance
+ */
+ public static ErSambuconeClient getInstance() {
+ return INSTANCE;
+ }
