+ private static List<Class<?>> getEventHierarchy(Class<?> eventClass) {
+ // Check cache first
+ List<Class<?>> hierarchy = eventHierarchyCache.get(eventClass);
+ if (hierarchy != null) {
+ return hierarchy;
+ }
+ 
+ // Build hierarchy
+ hierarchy = new ArrayList<>();
+ hierarchy.add(eventClass);
+ 
