+ if (executor != null && !executor.isShutdown()) {
+ executor.shutdown();
+ }
+ 
+ executor = Executors.newSingleThreadScheduledExecutor();
+ 
+ // Schedule the clicking task
+ executor.scheduleAtFixedRate(() -> {
+ try {
+ if (!isEnabled()) {
+ stopClicker();
+ return;
+ }
+ 
+ // Check if the game is in focus
